import React, { useState } from "react";
import TodoForm from "../todo-form/TodoForm";
import TodoList from "../todo-list/TodoList";
import styles from "./todoApp.module.css";

const TodoApp = () => {
  const [todos, setTodos] = useState([]);

  const addTodoHandler = (getTodo) => {
    const newTodo = {
      id: Math.floor(Math.random() * 1000),
      text: getTodo,
      isCompleted: false
    };
    setTodos([...todos, newTodo]);
  };

  const completeHandler = todoId => {
    //find index todo
    const indexTodo = todos.findIndex(todo => todo.id === todoId)
    //cloning object, because we have not mutate
    const selectedTodos = {...todos[indexTodo]}
    selectedTodos.isCompleted = !selectedTodos.isCompleted

    const updatedTodos = [...todos]
    updatedTodos[indexTodo] = selectedTodos
    setTodos(updatedTodos)
  }

  return (
    <div className={styles.container}>
      <TodoForm addTodoHandler={addTodoHandler} />
      <div className={styles.todoListContainer}>
        <TodoList todos={todos} onComplete={completeHandler} />
      </div>
    </div>
  );
};

export default TodoApp;
